mapper.select:
select n.resourceid, n.resourcetitle, n.resourcetypeid, n.resourceimage, n.resourceipicture, n.resourceviedo, n.resourceboards, n.resourcelink, n.opentype, n.openday, n.opentime, n.address, n.longitude, n.latitude, n.hostunit, n.maxamount, n.seqno, n.upflag, n.status, n.creator, n.createtime, n.updater, n.updatetime, n.auditor, n.audittime, n.auditdesc,n.resourcedetail, t.resourtypename from t_resource_site n left join t_resource_type t on n.resourcetypeid=t.resourcetypeid
and n.resourceid=#{resourceid}
and n.status=#{status}
and n.resourcetypeid=#{resourcetypeid}
and n.resourcetitle like CONCAT('%',#{resourcetitle},'%')
and t.resourtypename like CONCAT('%',#{resourtypename},'%')
order by n.updatetime desc
